#include <iostream>\n#include <string>\n#include <algorithm>\n\nclass Solution {\npublic:\n    std::string reverseString(std::string s) {\n        std::reverse(s.begin(), s.end());\n        return s;\n    }\n};\n\nint main() {\n    std::string input_s;\n    std::getline(std::cin, input_s);\n\n    Solution sol;\n    std::string reversed_s = sol.reverseString(input_s);\n\n    std::cout << reversed_s << std::endl;\n\n    return 0;\n}",}